<?php
 namespace plugins\Alipay\DirectPay\Funcs; class Md5{ public function md5Sign($prestr, $key) { $prestr = $prestr . $key; return md5($prestr); } public function md5Verify($prestr, $sign, $key) { $prestr = $prestr . $key; $mysgin = md5($prestr); if($mysgin == $sign) { return true; } else { return false; } } } ?>