<?php
 class AlipayTradeRefundContentBuilder { private $outTradeNo; private $tradeNo; private $refundAmount; private $refundReason; private $outRequestNo; private $bizContentarr = array(); private $bizContent = NULL; public function getBizContent() { if(!empty($this->bizContentarr)){ $this->bizContent = json_encode($this->bizContentarr,JSON_UNESCAPED_UNICODE); } return $this->bizContent; } public function getTradeNo() { return $this->tradeNo; } public function setTradeNo($tradeNo) { $this->tradeNo = $tradeNo; $this->bizContentarr['trade_no'] = $tradeNo; } public function getOutTradeNo() { return $this->outTradeNo; } public function setOutTradeNo($outTradeNo) { $this->outTradeNo = $outTradeNo; $this->bizContentarr['out_trade_no'] = $outTradeNo; } public function getRefundAmount() { return $this->refundAmount; } public function setRefundAmount($refundAmount) { $this->refundAmount = $refundAmount; $this->bizContentarr['refund_amount'] = $refundAmount; } public function getRefundReason() { return $this->refundReason; } public function setRefundReason($refundReason) { $this->refundReason = $refundReason; $this->bizContentarr['refund_reason'] = $refundReason; } public function getOutRequestNo() { return $this->outRequestNo; } public function setOutRequestNo($outRequestNo) { $this->outRequestNo = $outRequestNo; $this->bizContentarr['out_request_no'] = $outRequestNo; } } ?>