<?php
 namespace plugins\Wxpay; use plugins\Wxpay\Core\Data\WxPayBizPayUrl; use plugins\Wxpay\Core\Data\WxPayUnifiedOrder; class WxPayNativePay extends AbstractLib{ public function GetPrePayUrl($productId) { $biz = new WxPayBizPayUrl(); $biz->SetProduct_id($productId); $values = $this->getWxPayApi()->bizpayurl($biz); $url = "weixin://wxpay/bizpayurl?" . $this->ToUrlParams($values); return $url; } private function ToUrlParams($urlObj) { $buff = ""; foreach ($urlObj as $k => $v) { $buff .= $k . "=" . $v . "&"; } $buff = trim($buff, "&"); return $buff; } public function GetPayUrl(WxPayUnifiedOrder $input) { if($input->GetTrade_type() == "NATIVE") { $result = $this->getWxPayApi()->unifiedOrder($input); return $result; } } }